import React, { useState, useEffect } from 'react';
import '../App.css'; // fix this path if needed
import axios from 'axios';

function BookAppointment() {
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    physiotherapist: '',
    appointment_date: '',
    appointment_time: ''
  });
  const [status, setStatus] = useState('');
  const [doctors, setDoctors] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/physiotherapists')
      .then(res => setDoctors(res.data))
      .catch(err => console.error('Error fetching doctors:', err));
  }, []);

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = e => {
    e.preventDefault();
    axios.post('http://localhost:5000/appointments', formData)
      .then(() => setStatus('Appointment booked successfully.'))
      .catch(() => setStatus('Error booking appointment.'));
  };

  return (
    <div className="form-container">
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <label>Full Name:</label>
        <input name="patient_name" value={formData.patient_name} onChange={handleChange} required />

        <label>Email:</label>
        <input name="email" type="email" value={formData.email} onChange={handleChange} required />

        <label>Choose Physiotherapist:</label>
        <select name="physiotherapist" value={formData.physiotherapist} onChange={handleChange} required>
          <option value="">-- Select --</option>
          {doctors.map(doc => (
            <option key={doc.id} value={doc.name}>{doc.name}</option>
          ))}
        </select>

        <label>Appointment Date:</label>
        <input name="appointment_date" type="date" value={formData.appointment_date} onChange={handleChange} required />

        <label>Appointment Time:</label>
        <input name="appointment_time" type="time" value={formData.appointment_time} onChange={handleChange} required />

        <button type="submit">Submit</button>
        <div className="status-msg">{status}</div>
      </form>
    </div>
  );
}

export default BookAppointment;
