import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookAppointment = () => {
  const [doctors, setDoctors] = useState([]);
  const [selectedDoctorId, setSelectedDoctorId] = useState(null);
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    appointment_date: '',
    appointment_time: '',
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/physiotherapists')
      .then(res => setDoctors(res.data))
      .catch(err => {
        console.error('Error fetching doctors:', err);
        setMessage('Failed to load doctors');
      });
  }, []);

  const handleInputChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleBook = () => {
    if (!selectedDoctorId) {
      setMessage('Please select a doctor.');
      return;
    }

    const payload = {
      ...formData,
      physiotherapist_id: selectedDoctorId,
    };

    axios.post('http://localhost:5000/book', payload)
      .then(res => {
        setMessage('Appointment booked successfully!');
        setFormData({
          patient_name: '',
          email: '',
          appointment_date: '',
          appointment_time: '',
        });
        setSelectedDoctorId(null);
      })
      .catch(err => {
        console.error('Error booking appointment:', err);
        setMessage('Booking failed. Try again.');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Book an Appointment</h2>

      <div>
        <h3>Select a Physiotherapist:</h3>
        {doctors.map(doc => (
          <button
            key={doc.id}
            onClick={() => setSelectedDoctorId(doc.id)}
            style={{
              margin: '5px',
              padding: '10px',
              backgroundColor: selectedDoctorId === doc.id ? 'purple' : '#eee',
              color: selectedDoctorId === doc.id ? 'white' : 'black',
              border: '1px solid #ccc',
              borderRadius: '5px',
              cursor: 'pointer'
            }}
          >
            {doc.name} ({doc.specialization})
          </button>
        ))}
      </div>

      <div style={{ marginTop: '20px' }}>
        <label>Patient Name:</label><br />
        <input
          name="patient_name"
          value={formData.patient_name}
          onChange={handleInputChange}
        /><br /><br />

        <label>Email:</label><br />
        <input
          name="email"
          value={formData.email}
          onChange={handleInputChange}
        /><br /><br />

        <label>Appointment Date:</label><br />
        <input
          type="date"
          name="appointment_date"
          value={formData.appointment_date}
          onChange={handleInputChange}
        /><br /><br />

        <label>Appointment Time:</label><br />
        <input
          type="time"
          name="appointment_time"
          value={formData.appointment_time}
          onChange={handleInputChange}
        /><br /><br />

        <button onClick={handleBook}>Book Appointment</button>

        {message && <p style={{ marginTop: '15px', color: 'green' }}>{message}</p>}
      </div>
    </div>
  );
};

export default BookAppointment;
