import React, { useState } from 'react';
import '../App.css';

const BookAppointment = () => {
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    physiotherapist: '',
    appointment_date: '',
    appointment_time: ''
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:3001/api/appointments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        alert("Appointment booked!");
        setFormData({
          patient_name: '',
          email: '',
          physiotherapist: '',
          appointment_date: '',
          appointment_time: ''
        });
      } else {
        alert("Failed to book appointment.");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("Server error.");
    }
  };

  return (
    <div className="form-container">
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="patient_name" placeholder="Patient Name" value={formData.patient_name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="text" name="physiotherapist" placeholder="Physiotherapist Name" value={formData.physiotherapist} onChange={handleChange} required />
        <input type="date" name="appointment_date" value={formData.appointment_date} onChange={handleChange} required />
        <input type="time" name="appointment_time" value={formData.appointment_time} onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default BookAppointment;
