import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Make sure this path is correct

function BookAppointment() {
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    physiotherapist: '',
    appointment_date: '',
    appointment_time: ''
  });

  const [physiotherapists, setPhysiotherapists] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/physiotherapists')
      .then(res => setPhysiotherapists(res.data))
      .catch(err => console.error('Error fetching physiotherapists:', err));
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:5000/appointments', formData)
      .then(res => {
        setMessage('Appointment booked successfully!');
        setFormData({
          patient_name: '',
          email: '',
          physiotherapist: '',
          appointment_date: '',
          appointment_time: ''
        });
      })
      .catch(err => {
        console.error(err);
        setMessage('Error booking appointment.');
      });
  };

  return (
    <div className="form-container">
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <label>Full Name:</label>
        <input
          type="text"
          name="patient_name"
          value={formData.patient_name}
          onChange={handleChange}
        />

        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
        />

        <label>Choose Physiotherapist:</label>
        <select
          name="physiotherapist"
          value={formData.physiotherapist}
          onChange={handleChange}
        >
          <option value="">-- Select --</option>
          {physiotherapists.map(doc => (
            <option key={doc.id} value={doc.name}>{doc.name}</option>
          ))}
        </select>

        <label>Appointment Date:</label>
        <input
          type="date"
          name="appointment_date"
          value={formData.appointment_date}
          onChange={handleChange}
        />

        <label>Appointment Time:</label>
        <input
          type="time"
          name="appointment_time"
          value={formData.appointment_time}
          onChange={handleChange}
        />

        <button type="submit">Submit</button>
      </form>
      {message && <p className="status-msg">{message}</p>}
    </div>
  );
}

export default BookAppointment;
