import React, { useState } from 'react';
import axios from 'axios';

const BookAppointment = () => {
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    physiotherapist: '',
    appointment_date: '',
    appointment_time: ''
  });

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/appointments', formData);
      alert('Appointment booked!');
    } catch (err) {
      alert('Error booking appointment');
    }
  };

  return (
    <div className="form-container">
      <h2>Book Appointment</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="patient_name" placeholder="Patient Name" required onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" required onChange={handleChange} />
        <input type="text" name="physiotherapist" placeholder="Physiotherapist" required onChange={handleChange} />
        <input type="date" name="appointment_date" required onChange={handleChange} />
        <input type="time" name="appointment_time" required onChange={handleChange} />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default BookAppointment;
