import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookAppointment() {
  const [form, setForm] = useState({
    name: '',
    email: '',
    date: '',
    time: '',
    physiotherapist: ''
  });

  const [doctors, setDoctors] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/physiotherapists')
      .then(res => setDoctors(res.data))
      .catch(err => console.error('Error fetching physiotherapists:', err));
  }, []);

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDoctorSelect = (name) => {
    setForm({ ...form, physiotherapist: name });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(form);
    // Optionally: submit form to backend
  };

  return (
    <div style={{ textAlign: 'center', paddingTop: '40px' }}>
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Full Name: </label>
          <input name="name" value={form.name} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Email: </label>
          <input name="email" value={form.email} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Select Physiotherapist:</label>
          <div style={{ margin: '10px 0' }}>
            {doctors.map((doc) => (
              <button
                key={doc.id}
                type="button"
                onClick={() => handleDoctorSelect(doc.name)}
                style={{
                  margin: '5px',
                  padding: '8px 12px',
                  backgroundColor: form.physiotherapist === doc.name ? '#6a0dad' : '#eee',
                  color: form.physiotherapist === doc.name ? '#fff' : '#000',
                  border: '1px solid #ccc',
                  borderRadius: '5px',
                  cursor: 'pointer'
                }}
              >
                {doc.name} ({doc.specialization})
              </button>
            ))}
          </div>
        </div>
        <div>
          <label>Date: </label>
          <input type="date" name="date" value={form.date} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Time: </label>
          <input type="time" name="time" value={form.time} onChange={handleInputChange} required />
        </div>
        <button type="submit" style={{ marginTop: '10px' }}>Book</button>
      </form>
    </div>
  );
}

export default BookAppointment;
