import React, { useEffect, useState } from 'react';

function BookAppointment() {
  const [patientName, setPatientName] = useState('');
  const [email, setEmail] = useState('');
  const [appointmentDate, setAppointmentDate] = useState('');
  const [appointmentTime, setAppointmentTime] = useState('');
  const [physiotherapists, setPhysiotherapists] = useState([]);
  const [selectedPhysioId, setSelectedPhysioId] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/physiotherapists')
      .then((res) => res.json())
      .then((data) => setPhysiotherapists(data))
      .catch((err) => console.error("Failed to load physiotherapists", err));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!selectedPhysioId) {
      alert("Please select a physiotherapist.");
      return;
    }

    const appointment = {
      patient_name: patientName,
      email: email,
      physiotherapist_id: selectedPhysioId,
      appointment_date: appointmentDate,
      appointment_time: appointmentTime,
    };

    try {
      const res = await fetch('http://localhost:5000/book', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(appointment),
      });

      const message = await res.text();
      alert(message);
    } catch (err) {
      console.error("Error booking appointment:", err);
      alert("Failed to book appointment.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Full Name: </label>
          <input value={patientName} onChange={(e) => setPatientName(e.target.value)} required />
        </div>

        <div>
          <label>Email: </label>
          <input value={email} type="email" onChange={(e) => setEmail(e.target.value)} required />
        </div>

        <div>
          <label>Select Physiotherapist:</label>
          <div>
            {physiotherapists.map((doc) => (
              <div key={doc.id}>
                <input
                  type="radio"
                  id={`physio-${doc.id}`}
                  name="physio"
                  value={doc.id}
                  onChange={() => setSelectedPhysioId(doc.id)}
                  required
                />
                <label htmlFor={`physio-${doc.id}`}>
                  {doc.name} ({doc.specialization})
                </label>
              </div>
            ))}
          </div>
        </div>

        <div>
          <label>Date: </label>
          <input type="date" value={appointmentDate} onChange={(e) => setAppointmentDate(e.target.value)} required />
        </div>

        <div>
          <label>Time: </label>
          <input type="time" value={appointmentTime} onChange={(e) => setAppointmentTime(e.target.value)} required />
        </div>

        <button type="submit">Book</button>
      </form>
    </div>
  );
}

export default BookAppointment;
