import React, { useState, useEffect } from 'react';
import './App.css';

function BookAppointment() {
  const [formData, setFormData] = useState({
    patient_name: '',
    email: '',
    physiotherapist: '',
    appointment_date: '',
    appointment_time: '',
  });

  const [physios, setPhysios] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/physiotherapists')
      .then(res => res.json())
      .then(data => setPhysios(data))
      .catch(err => console.error(err));
  }, []);

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:5000/appointments', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    })
      .then(res => res.json())
      .then(data => alert(data.message))
      .catch(err => alert('Error: ' + err.message));
  };

  return (
    <div className="form-container">
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit} className="appointment-form">
        <label>
          Full Name:
          <input type="text" name="patient_name" value={formData.patient_name} onChange={handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </label>
        <label>
          Choose Physiotherapist:
          <select name="physiotherapist" value={formData.physiotherapist} onChange={handleChange} required>
            <option value="">-- Select --</option>
            {physios.map(p => (
              <option key={p.id} value={p.name}>{p.name}</option>
            ))}
          </select>
        </label>
        <label>
          Appointment Date:
          <input type="date" name="appointment_date" value={formData.appointment_date} onChange={handleChange} required />
        </label>
        <label>
          Appointment Time:
          <input type="time" name="appointment_time" value={formData.appointment_time} onChange={handleChange} required />
        </label>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default BookAppointment;
