import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookAppointment() {
  const [physiotherapists, setPhysiotherapists] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    physiotherapistId: '',
    date: '',
    time: ''
  });

  // Fetch physiotherapists from backend
  useEffect(() => {
    axios.get('http://localhost:5000/physiotherapists')
      .then(res => {
        setPhysiotherapists(res.data);
      })
      .catch(err => {
        console.error('Error fetching physiotherapists:', err);
      });
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Submit appointment
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitting appointment:', formData);

    axios.post('http://localhost:5000/appointments', formData)
      .then(res => {
        alert('Appointment booked successfully!');
        setFormData({
          name: '',
          email: '',
          physiotherapistId: '',
          date: '',
          time: ''
        });
      })
      .catch(err => {
        console.error('Booking error:', err);
        alert('Failed to book appointment');
      });
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Full Name: </label>
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </div>

        <div>
          <label>Email: </label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} required />
        </div>

        <div>
          <label>Select Physiotherapist: </label>
          <select name="physiotherapistId" value={formData.physiotherapistId} onChange={handleChange} required>
            <option value="">-- Select --</option>
            {physiotherapists.map(doc => (
              <option key={doc.id} value={doc.id}>
                {doc.name} ({doc.specialization})
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Date: </label>
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </div>

        <div>
          <label>Time: </label>
          <input type="time" name="time" value={formData.time} onChange={handleChange} required />
        </div>

        <div>
          <button type="submit">Book</button>
        </div>
      </form>
    </div>
  );
}

export default BookAppointment;
