import React, { useEffect, useState } from 'react';
import axios from 'axios';

function BookAppointment() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [physiotherapists, setPhysiotherapists] = useState([]);
  const [selectedPhysio, setSelectedPhysio] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/physiotherapists')
      .then(res => setPhysiotherapists(res.data))
      .catch(err => console.error('Error fetching physiotherapists:', err));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!selectedPhysio) {
      alert('Please select a physiotherapist.');
      return;
    }

    const appointment = {
      name,
      email,
      physiotherapist_id: selectedPhysio.id,
      date,
      time
    };

    axios.post('http://localhost:5000/appointments', appointment)
      .then(() => alert('Appointment booked successfully!'))
      .catch(err => {
        console.error('Error booking appointment:', err);
        alert('Booking failed.');
      });
  };

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h2>Book an Appointment</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Full Name: </label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div><br />
        <div>
          <label>Email: </label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div><br />
        <div>
          <label>Select Physiotherapist:</label><br />
          {physiotherapists.map((physio) => (
            <button
              key={physio.id}
              type="button"
              onClick={() => setSelectedPhysio(physio)}
              style={{
                margin: '5px',
                backgroundColor: selectedPhysio?.id === physio.id ? '#6a0dad' : '#ccc',
                color: selectedPhysio?.id === physio.id ? 'white' : 'black',
                padding: '10px',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer'
              }}
            >
              {physio.name} ({physio.specialization})
            </button>
          ))}
        </div><br />
        <div>
          <label>Date: </label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div><br />
        <div>
          <label>Time: </label>
          <input
            type="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div><br />
        <button type="submit">Book</button>
      </form>
    </div>
  );
}

export default BookAppointment;
